create database sistemaGM;
use sistemaGM;

	CREATE TABLE `contasReceber` (
  `idcontasReceber` INT AUTO_INCREMENT PRIMARY KEY,
  `aluno` VARCHAR(100) NOT NULL,
  `responsavel_financeiro` VARCHAR(255) NOT NULL,
  `telefone` VARCHAR(20) NOT NULL,
  `vencimento` varchar(100) NOT NULL,
  `contaBancaria` VARCHAR(30) NOT NULL,
  `descricao` VARCHAR(150) NOT NULL,
  `valor` DECIMAL(10,2) NOT NULL,
  `status` VARCHAR(50) NOT NULL
);




CREATE TABLE vendas ( 
  codigoVenda INT AUTO_INCREMENT NOT NULL,
  dataHora varchar(50) NOT NULL,
  numeroIdentCarro varchar(17) NOT NULL,
  cliente VARCHAR(200) NOT NULL,
  cpfFuncionario varchar(11) NOT NULL,
  qtdProdutos INT NOT NULL,
  valorUnidade decimal(60,2) NOT NULL,
  metodoPagamento VARCHAR(50) NOT NULL,
  endereco VARCHAR(100) NOT NULL,
  status VARCHAR(25) NOT NULL,
  CONSTRAINT `PRIMARY` PRIMARY KEY (codigoVenda)
);




CREATE TABLE `fornecedores` ( 
  `idFornecedor` INT AUTO_INCREMENT NOT NULL,
  `representanteEmpresa` varchar(70) not null,
  `telefoneRepresentante` varchar(20) not null,
  `cargoRepresentante`varchar(50) not null,
  `cpfRepresentante` varchar(20) not null,
  `nomeEmpresa` VARCHAR(150) NOT NULL,
  `email` VARCHAR(150) NOT NULL,
  `telefoneEmpresa` VARCHAR(20) NOT NULL,
  `cnpj` VARCHAR(20) NOT NULL,
  `endereco` VARCHAR(250) NOT NULL,
  `produtoServico` VARCHAR(100) ,
  CONSTRAINT `PRIMARY` PRIMARY KEY (`idFornecedor`)
);





CREATE TABLE funcionarios ( 
  idFuncionario INT AUTO_INCREMENT NOT NULL,
  nome VARCHAR(150) NOT NULL, 
  cpf VARCHAR(20) NOT NULL,
  cargo VARCHAR(40) NOT NULL,
  RegimeDeTrabalho VARCHAR(15) NOT NULL,
  telefone VARCHAR(20) NOT NULL,
  Salario VARCHAR(250) NOT NULL,
  email VARCHAR(150) NOT NULL,
  endereco VARCHAR(100) NOT NULL,
  CONSTRAINT pk_funcionarios PRIMARY KEY (idFuncionario)
);




CREATE TABLE `alunos` ( 
  `idAluno` INT AUTO_INCREMENT NOT NULL,
  `nome` VARCHAR(100) NOT NULL,
  `email` VARCHAR(150) NOT NULL,
  `telefone` VARCHAR(20) NOT NULL,
  `cpf` VARCHAR(20) NOT NULL,
  `endereco` VARCHAR(250) NOT NULL,
  `dataNascimento` DATE NOT NULL,
  `matricula` VARCHAR(50) NOT NULL,
  `anoLetivo` VARCHAR(100) NOT NULL,
  `periodo` VARCHAR(50),
  CONSTRAINT `PRIMARY` PRIMARY KEY (`idAluno`)
);




CREATE TABLE `cadastro` ( 
  `idCadastro` INT AUTO_INCREMENT NOT NULL,
  `nome` VARCHAR(150) NOT NULL,
  `email` VARCHAR(150) NOT NULL,
  `telefone` VARCHAR(20) NOT NULL,
  `cpf` VARCHAR(20) NOT NULL,
  `endereco` VARCHAR(250) NOT NULL,
  `senha` VARCHAR(45) NOT NULL,
  CONSTRAINT `PRIMARY` PRIMARY KEY (`idCadastro`)
);



CREATE TABLE `Boletim` ( 
  `idBoletim` INT AUTO_INCREMENT NOT NULL,
  `nomeAluno` VARCHAR(150) NOT NULL,
  `semestre` VARCHAR (20) NOT NULL,
  `materia` VARCHAR(150) NOT NULL,
  `nota1` decimal(10,1) NOT NULL,
  `nota2` decimal(10,1) NOT NULL,
  `nota3` decimal(10,1) NOT NULL,
  `notaFinal`decimal(10,1) NOT NULL,
  `situacao` VARCHAR(10) NOT NULL,
  CONSTRAINT `PRIMARY` PRIMARY KEY (`idBoletim`)
);



CREATE TABLE `FuncionariosTerceirizados` (
    `idFuncionarioTercerizado` INT AUTO_INCREMENT  NOT NULL,
    `nome` VARCHAR(100) NOT NULL,
	`email` VARCHAR(100),
	`telefone` VARCHAR(15),
    `cpf` VARCHAR(14) NOT NULL,
    `cargo` VARCHAR(50) NOT NULL,
	`regime_trabalho` VARCHAR(10) NOT NULL,
	`endereco` VARCHAR(250) NOT NULL,
	`empresa_terceirizada` VARCHAR(100) NOT NULL,
  CONSTRAINT `PRIMARY` PRIMARY KEY (idFuncionarioTercerizado)
);





create table contasPagar (
`idContasPagar` int auto_increment primary key,
`descricao` varchar(255)not null,
`categoria` enum('Salário', 'Aluguel', 'Luz', 'Água', 'Internet', 'Material Escolar', 'Manutenção') not null,
`nomePagamento` varchar(100)not null,
`dataEmissao` date not null,
`dataVencimento` date not null,
`valor` decimal(10, 2) not null,
`parcelamento` int default 1,
`status` enum('Pendente', 'Paga', 'Atrasada', 'Cancelada') not null
);













